// functions
function drawChartBarVerticle() {
    drawChart(data, {
        yAxisConfig: { yaxisStatus: true },
        xAxisConfig: { xaxisStatus: true },
        dataLabelColor: "#000000",
        paddingInner: 0.25,
        innerRadius: 150,
        chartType: "FitWidth",
        dataColumns: {
            xAxisColumnDetails: [
                {
                    itemName: "Product_Name",
                    itemType: "Categorical",
                    dateFormat: "%Y"
                }
            ],
            yAxisColumnDetails: [
                {
                    itemName: "Sales",
                    itemType: "Numerical",
                    dateFormat: "%Y"
                }
            ],
            row3ColumnDetails: [],
            colorByData: []
        }
    });
}

function redrawChartBarVerticle() {
    var timer;
    window.addEventListener("resize", function () {
        clearTimeout(timer);
        timer = setTimeout(function () {
            window.clearChart && clearChart();
            drawChart(data, {
                yAxisConfig: { yaxisStatus: true },
                xAxisConfig: { xaxisStatus: true },
                dataLabelColor: "#000000",
                paddingInner: 0.25,
                innerRadius: 150,
                chartType: "Standard",
                dataColumns: {
                    xAxisColumnDetails: [
                        {
                            itemName: "Product_Name",
                            itemType: "Categorical",
                            dateFormat: "%Y"
                        }
                    ],
                    yAxisColumnDetails: [
                        {
                            itemName: "Sales",
                            itemType: "Numerical",
                            dateFormat: "%Y"
                        }
                    ],
                    row3ColumnDetails: [],
                    colorByData: []
                }
            });
        }, 0);
    });
}
